import React, { useState } from "react"
import { AiFillDownCircle } from 'react-icons/ai'
import { IoIosAddCircle } from 'react-icons/io'
import { AddProdImage as img } from '../../../NavigationMenu'

function AddProduct() {

    const [{ alt, src }, setImg] = useState({
        src: '../images/product/1.png',
        alt: "Upload Image"
    })
    // concordion 
    const [clicked, setClicked] = useState(false)

    const toggle = index => {
        if (clicked === index) {
            setClicked(null)
        }
        setClicked(index)
    }

    // end of concordion

    // trigger file upload
    let fileRef = React.createRef()

    const handleImg = (e) => {
        console.log(e.target)

        if (e.target.files[0]) {
            setImg({
                src: URL.createObjectURL(e.target.files[0]),
                alt: e.target.files[0].name
            })
        }

    }
    const tiggerClick = () => {
        fileRef.current.click()
    }
    return (
        <div className="mt-[90px] mb-20">

            <button className="ml-[210px] border-2 border-[#f98a8d] bg-[#f0666a] text-white" style={{
                borderTopRightRadius: '10px',
                borderBottomRightRadius: '10px',
                borderTopLeftRadius: '10px',
                borderBottomLeftRadius: '10px',
                outline: 'none',
            }}> Add Product</button>
            <form encType="multipart/form-data">

                <div className="mt-10 space-y-6">


                    <div className="ml-[40px]">

                        {
                            img.map((item, index) => {
                                return (
                                    <>
                                        <div className="space-y-2">
                                            <div className="p-1">
                                                <div className="border-b-4 h-[30px] bg-red-300 w-[250px] flex" onClick={() => toggle(index)} key={index}>
                                                    <span>{item.title}</span>
                                                    <span>
                                                        {
                                                            clicked === index ?

                                                                <AiFillDownCircle className="text-[#FFF] ml-[9.5rem] mt-[2px] w-[1.5em] h-[1.5em]" />

                                                                :

                                                                <IoIosAddCircle className="text-[#FFF] ml-[9.5rem] mt-[2px] w-[1.5em] h-[1.5em]" />
                                                        }
                                                    </span>
                                                </div>
                                            </div>

                                            {
                                                clicked === index ? (
                                                    <>
                                                        <label className="">{item.imgfor}</label>
                                                        <div className="h-[250px] w-[250px] mt-[-100em] border-4 border-[#f98a8d]" style={{
                                                            borderTopRightRadius: '10px',
                                                            borderBottomRightRadius: '10px',
                                                            borderTopLeftRadius: '10px',
                                                            borderBottomLeftRadius: '10px',
                                                            outline: 'none',
                                                        }}>
                                                            <input
                                                                type="file"
                                                                accept=".png, .jpg, .jpeg"
                                                                id="photo"
                                                                ref={fileRef}
                                                                className=""
                                                                onChange={handleImg}
                                                                hidden
                                                            />

                                                            <img
                                                                src={src}
                                                                alt={alt}
                                                                onClick={tiggerClick}
                                                                className="h-[242px] w-[242px]"

                                                            />

                                                        </div>

                                                    </>

                                                ) : null}

                                        </div>
                                    </>
                                )

                            }
                            )}

                    </div>



                    <div className="prodinput">
                        <label className="text-sm ">*Item Name</label>
                        <input className="h-10 w-[280px] pdinput" type="text" placeholder="laptop" />
                    </div>
                    <div className="prodinput">
                        <label className="text-sm ">*Quantity</label>
                        <input className="h-10 w-[280px] pdinput" type="number" placeholder="20" />
                    </div>
                    <div className="prodinput">
                        <label className="text-sm ">*Cost Price</label>
                        <input className="h-10 w-[280px] pdinput" type="text" placeholder="Item name" />
                    </div>
                    <div className="prodinput">
                        <label className="text-sm ">*Sale Price</label>
                        <input className="h-10 w-[280px] pdinput" type="text" placeholder="Item name" />
                    </div>
                    <div className="prodinput">
                        <label className="text-sm ">*Product Category</label>
                        <select className="h-10 w-[280px] pdinput">
                            <option>Select category</option>
                            <option>Electronic</option>
                            <option>Beverage</option>
                            <option>Clothing</option>
                            <option>Automobile</option>
                            <option>Space</option>
                            <option>Edible</option>
                        </select>

                    </div>

                    <button className="text-[#fff] text-xl border-2 ml-[100px] bg-[#f0666a] w-[180px] h-[50px] justify-center" style={{
                        borderTopRightRadius: '10px',
                        borderBottomRightRadius: '10px',
                        borderTopLeftRadius: '10px',
                        borderBottomLeftRadius: '10px',
                        outline: 'none',
                    }}> Add To Product</button>

                </div>

            </form>
        </div>
    )
}

export default AddProduct
