import React, { useState } from "react"
import { AiFillDownCircle } from 'react-icons/ai'
import { IoIosAddCircle } from 'react-icons/io'
import { AddProdImage as img } from '../../../NavigationMenu'

function AddProduct() {

    const [file, setFile] = useState([])
    const upLoadImg = (e) => {
        setFile([...file, URL.createObjectURL(e.target.files[0])])
        console.log("file", file)
    }



    return (
        <div className="mt-[90px] mb-20">


            <button className="ml-[210px] border-2 border-[#f98a8d] bg-[#f0666a] text-white" style={{
                borderTopRightRadius: '10px',
                borderBottomRightRadius: '10px',
                borderTopLeftRadius: '10px',
                borderBottomLeftRadius: '10px',
                outline: 'none',
            }}> Add Product</button>
            <form encType="multipart/form-data">

                <div className="mt-10 space-y-6">
                    {/* image  preview */}


                    <div className="">
                        <input
                            type="file"
                            disabled={file.length === 5}
                            className=""
                            onChange={upLoadImg}
                        />
                        <div className="">
                            {file.length > 0 && file.map((item, index) => {
                                return (
                                    <div className="" key={item}>
                                    <img
                                        src={item}
                                        className="h-[242px] w-[242px]"
                                    />
                                    <button>
                                        Delete
                    </button>
                                </div>

                                // <div key={item} className="h-[250px] w-[250px] mt-[-100em] border-4 border-[#f98a8d]" style={{
                                //     borderTopRightRadius: '10px',
                                //     borderBottomRightRadius: '10px',
                                //     borderTopLeftRadius: '10px',
                                //     borderBottomLeftRadius: '10px',
                                //     outline: 'none',
                                // }}>


                                //     <img
                                //         src={item}
                                //         className="h-[242px] w-[242px]"

                                //     />

                                // </div>


                                )

                            })}
                        </div>

                    </div>

                    <div className="prodinput">
                        <label className="text-sm ">*Item Name</label>
                        <input className="h-10 w-[280px] pdinput" type="text" placeholder="laptop" />
                    </div>
                    <div className="prodinput">
                        <label className="text-sm ">*Quantity</label>
                        <input className="h-10 w-[280px] pdinput" type="number" placeholder="20" />
                    </div>
                    <div className="prodinput">
                        <label className="text-sm ">*Cost Price</label>
                        <input className="h-10 w-[280px] pdinput" type="text" placeholder="Item name" />
                    </div>
                    <div className="prodinput">
                        <label className="text-sm ">*Sale Price</label>
                        <input className="h-10 w-[280px] pdinput" type="text" placeholder="Item name" />
                    </div>
                    <div className="prodinput">
                        <label className="text-sm ">*Product Category</label>
                        <select className="h-10 w-[280px] pdinput">
                            <option>Select category</option>
                            <option>Electronic</option>
                            <option>Beverage</option>
                            <option>Clothing</option>
                            <option>Automobile</option>
                            <option>Space</option>
                            <option>Edible</option>
                        </select>

                    </div>

                    <button className="text-[#fff] text-xl border-2 ml-[100px] bg-[#f0666a] w-[180px] h-[50px] justify-center" style={{
                        borderTopRightRadius: '10px',
                        borderBottomRightRadius: '10px',
                        borderTopLeftRadius: '10px',
                        borderBottomLeftRadius: '10px',
                        outline: 'none',
                    }}> Add To Product</button>

                </div>

            </form>
        </div>
    )
}

export default AddProduct
