/*
|--------------------------------------------------------------------------
| A collection of Footer components.
|--------------------------------------------------------------------------
|
| Use any one you like. Once you decided for one, you might want to consider
| deleting the others to keep you javascript bundle size as small as possible.
|
*/
import Link from "next/link";
import PropTypes from "prop-types";
import {ReactElement} from "react";
import {AiFillGithub} from 'react-icons/ai'
import {FaTwitter} from 'react-icons/fa'
import {AiOutlineMediumWorkmark} from 'react-icons/ai'
import {ImFacebook} from 'react-icons/im'

/**
 * Grid layout. Defaults to three colums per row.
 */
export function AdvancedFooter(): ReactElement {
    // Customize to your needs. The footer ui will be composed of this data.
    const footerData: { headline: string, content: { title: string, link: string }[] }[] = [
        // First col.
        {
            headline: "Links",
            content: [
                {title: "Contact-Us", link: "/contact"},
                {title: "Article", link: "/articles"},
                {title: "Product", link: "/ourproduct"},
                {title: "About", link: "/about"},
                {title: "Join", link: "/join"},
            ],
        },  
        // Second col.
        // {
        //     headline: "Social Media",
        //     content: [
        //         {title: "Twitter", link: "https://twitter.com/moxieusT"},
        //         {
        //             title: "GitHub",
        //             link:
        //                 "https://github.com/NiclasTimmeDev/laravel-nextjs-starter/",
        //         },
        //     ],
        // },
        // Third col.
        // {
        //     headline: "Legal",
        //     content: [
        //         {title: "Privacy", link: "/privacy"},
        //         {title: "Imprint", link: "/imprint"},
        //     ],
        // },
    ];

    /**
     * Map over the footerData array. For every object in the array,
     * return a new div with a headline and as many links as defined
     * in the .content of the currently mapped over object.
     */
    const footerCols: JSX.Element[] = footerData.map((element) => {
        return (

            <>

            <div className="flex space-x-10 text-white ml-4 absolute ">
             <AiFillGithub className="text-2xl"/>
             <FaTwitter className="text-2xl"/>
             <AiOutlineMediumWorkmark className="text-2xl"/>
             <ImFacebook className="text-xl"/>
            </div>
            <div key={element.headline} className=" flex space-x-4 ml-[2em] text-black">
                {/* Map over every entry in the content and return an footer link component. */}
                {element.content.map((link) => {
                    return (
                        <FooterLink
                            key={link.link}
                            title={link.title}
                            link={link.link}
                        />
                    );
                })}
            </div>

            </>
        );
    });

    return (
        <footer className=" advanced-footer w-screen py-4 px-2 bg-[#181c22]  h-[10em]">
            <div className=" container mx-auto w-full grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-2 gap-28">
                {footerCols}    
            </div>
        </footer>
    );
}

/**
 * A simple footer component.
 *
 * @return ReactElement
 */
export function SimpleFooter(): ReactElement {
    const currentYear: number = new Date().getFullYear();

    /**
     * All links that will be displayed in the footer.
     * Customize to your requirements.
     */
    const footerLinks = [
        {title: "Imprint", link: "/imprint"},
        {title: "Privacy Statement", link: "/privacy"},
    ];

    // A FooterLink component for every given link.
    const footerItems: ReactElement[] = footerLinks.map((link) => {
        return (
            <FooterLink
                key={link.title}
                title={link.title}
                link={link.link}
                marginLeft={true}
            />
        );
    });

    // Return statement.
    return (
        <footer className="simple-footer w-screen py-4 px-2 bg-[#181c22]">
            <div className="w-full flex justify-between items-center">
                <span className="text-black">Copyright {currentYear}</span>
                <div className="flex items-center">{footerItems}</div>
            </div>
        </footer>

    );
}

/**
 * A link that will be displayed in the Footer.
 *
 * @param {object} props
 *   The props, including title and link.
 */
export function FooterLink({title, link, marginLeft}): ReactElement {
    return (
        <Link href={link}>
            <h5
                className={`text-white hover:text-purple-700 hover:underline cursor-pointer ${
                    marginLeft ? "ml-4" : ""
                }`}
            >
                {title}
            </h5>
        </Link>
    );
}

FooterLink.propTypes = {
    title: PropTypes.string.isRequired,
    link: PropTypes.string.isRequired,
    marginLeft: PropTypes.bool,
};
